from aitool import load_csv, timestamp, dump_excel, AutoPrompt
from random import sample


def get_dataset():
    # 开源数据来源：https://github.com/dut-lujunyu/toxicn
    raw_data = load_csv('./ToxiCN/ToxiCN_1.0.csv', to_list=True)
    dump_excel(raw_data, './ToxiCN/ToxiCN_1.0.xlsx')
    raw_data = [(line[1], 'good', line[2], '') for line in raw_data]
    train_data = sample(raw_data[:1000], 200)
    test_data = sample(raw_data[1000:], 300)
    return train_data, test_data


